import { useEffect } from "react";

const InitialState = {
   Books: [],
   DetailBook: {},
   Author:{}
  };
  
function Reducer(state = InitialState, action) {

    switch (action.type) { 
      case "ALL_BOOKS":
        return { ...state,
        Books: action.payload 
        };
      case "DETAIL_BOOK":
        return {
          ...state,
          DetailBook: action.payload
        };
        case "AUTHOR":
          return { ...state,
          Author: action.payload
          };
      default:
        return state;
    }
  }
  
  export default Reducer;
