import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux"
import {CrearA} from '../Redux/Actions'

const styleButton = {
    borderRadius: "999px",
    backgroundColor: "white",
    margin: "20px",
    outline: "none",
    fontFamily: "fantasy",
    height:'37px',
    width:'85px',
    fontSize:'11px'
  };

  const InputStyle = {
    borderRadius: "20px",
    border: "0",
    width: "175px",
    height: "30px",
    padding: "7px",
    outline: "none",
    marginRight: "20px",
  };


export function CreateA() {
const dispatch = useDispatch()

const [Estado, setEstado] = useState({first_name: "", last_name:""})

const handleChange = (event) => {
    let value = event.target.value;
    let name = event.target.name;
  
    setEstado((prevalue) => {
      return {
        ...prevalue,   // Spread Operator               
        [name]: value
      }
    })
  }

function  handlesubmit(e) {
e.preventDefault();

if(!Estado.first_name){
  return alert('Debes elgir un Nombre')
}
else if(!Estado.last_name){
    return alert('Debes elgir un Nombre')
}

dispatch(CrearA(Estado));
setEstado({first_name:'', last_name:''});
alert('Author creado');
}

    return (
        <form>
        <div>
            <input style={InputStyle} name="name" onChange={handlechange} value={Estado.first_name} placeholder='name'/>
            <input style={InputStyle} name='hp' onChange={handlechange} value={Estado.last_name} placeholder='hp'/>
            <button style={styleButton} onClick={(e) => handlesubmit(e)}>Crear Author</button>
        </div>
        </form>
    )
}
